import { useState } from "react";
import Product from "../Product";

export default function Register() {
    const products = [
        { name: "Bolo de anivers√°rio", icon: "üéÇ", price: 20.9 },
        { name: "Bal√£o", icon: "üéà", price: 5.6 },
        { name: "Confete", icon: "üéâ", price: 2.9 },
        { name: "Suco de caixinha", icon: "üßÉ", price: 1.9 },
        { name: "Doces diversos", icon: "üç¨", price: 12.3 }
    ];
    const [total, setTotal] = useState(0)

    return (
        <>
        <ContainerRegister>
            <div className="register">
                <b>Total da compra:</b>
                R$<span>{total.toFixed(2).toString().replace(".", ",")}</span>
            </div>
            <div>
                {products.map((product, index) => {
                    const { name, icon, price } = product;
                    return (
                        <Product
                            name={name}
                            icon={icon}
                            price={price}
                            setTotal={setTotal}
                            total={total}
                            index={index}
                        />
                    )
                })}
            </div>
            </ContainerRegister>
        </>
    )
}
